import React, { useState } from 'react';
import { View, Text, Button, TextInput, Alert } from 'react-native';

const App = () => {
  const [consentGiven, setConsentGiven] = useState(false);
  const [data, setData] = useState('');
  const [loading, setLoading] = useState(false);

  const handleConsent = () => {
    Alert.alert('Consent Alert', 'Do you consent to sharing your data?', [
      {
        text: 'Yes', onPress: () => {
          setConsentGiven(true);
        },
      },
      { text: 'No', onPress: () => {} },
    ]);
  };

  const handleDataCollection = () => {
    if (consentGiven) {
      // Simulate data collection and transmission
      setLoading(true);
      setTimeout(() => {
        // Simulate successful transmission
        setLoading(false);
        Alert.alert('Success', 'Data transmitted successfully!');
      }, 2000);
    } else {
      Alert.alert('Consent Required', 'Please give consent to continue.');
    }
  };

  return (
    <View>
      <Text>Data Collection App</Text>
      <TextInput
        placeholder="Enter your data"
        value={data}
        onChangeText={setData}
      />
      <Button title="Collect and Send Data" onPress={handleDataCollection} disabled={!consentGiven} />
      <Button title="Give Consent" onPress={handleConsent} />
      {loading && <Text>Transmitting data...</Text>}
    </View>
  );
};

export default App;
```

