import string

def crack_password(password):
    # Check if the password length is less than 4 characters
    if len(password) < 4:
        print("Password is too short")
        return None

    # Check if the password contains only lowercase letters
    if not password.isalpha():
        print("Password must contain only lowercase letters")
        return None

    # Try common patterns
