        for char in string.ascii_lowercase:
            new_password = password[:i] + char + password[i+1:]
            if is_weak_password(new_password):
                return new_password
    return None

def crack_password_dictionary(password):
    with open("dictionary.txt", "r") as file:
        for word in file:
            if password == word.strip():
                return word.strip()
    return None

def is_weak_password(password):
    # Check if the password contains consecutive repeating characters
    for i in range(len(password) - 1):
        if password[i] == password[i+1]:
            return True
    return False

