import os
import shutil
import getpass

class SecureUserInfoManager:
    def __init__(self, filename='user_info.dat'):
        self.filename = filename
        self.data = self.load_data()

    def load_data(self):
        if os.path.exists(self.filename):
            with open(self.filename, 'rb') as file:
                return shutil.unpack_archive(file.read())
        return {}

    def save_data(self):
        with open(self.filename, 'wb') as file:
            file.write(shutil.pack_archive(self.data))

    def add_user(self, username, password, email):
        if username in self.data:
            print(f"Username '{username}' already exists. Please choose a different username.")
        else:
            self.data[username] = {'password': password, 'email': email}
            print("User information added successfully.")
            self.save_data()

    def list_users(self):
        if not self.data:
            print("No user information found.")
        else:
            print("User Information:")
            for username, info in self.data.items():
                print(f"Username: {username}, Email: {info['email']}")

    def remove_user(self, username):
        if username in self.data:
            del self.data[username]
            print("User information removed successfully.")
            self.save_data()
        else:
            print(f"Username '{username}' not found.")

    def main_menu():
        while True:
            print("\nSecure User Info Manager")
            print("1. Add User")
            print("2. List Users")
            print("3. Remove User")
            print("4. Exit")

            choice = input("Enter your choice: ")

            if choice == '1':
                username = input("Enter username: ")
                password = getpass.getpass("Enter password: ")
                email = input("Enter email: ")
                self.add_user(username, password, email)

            elif choice == '2':
                self.list_users()

            elif choice == '3':
                username = input("Enter username to remove: ")
                self.remove_user(username)

            elif choice == '4':
                print("Exiting...")
                break

            else:
                print("Invalid choice. Please select a valid option.")

    if __name__ == "__main__":
        main_menu()
```

