import android.os.Bundle;
import android.app.Activity;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Toast;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithm;
import java.util.ArrayList;
import java.util.List;

public class MainActivity extends Activity {

    private List<String> userData;
    private Button collectButton, accessButton;
    private EditText userInput;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        userData = new ArrayList<>();
        collectButton = findViewById(R.id.collect_button);
        accessButton = findViewById(R.id.access_button);
        userInput = findViewById(R.id.user_input);

        collectButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                String userConsent = userInput.getText().toString();
                if (userConsent.equals("consent")) {
                    String personalData = "Name: John Doe\nAge: 25\nEmail: johndoe@example.com";
                    userData.add(personalData);
                    Toast.makeText(MainActivity.this, "Data collected successfully!", Toast.LENGTH_SHORT).show();
                } else {
                    Toast.makeText(MainActivity.this, "Consent required to collect data.", Toast.LENGTH_SHORT).show();
                }
            }
        });

        accessButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                if (userData.size() > 0) {
                    String encryptedData = encryptData(userData);
                    Toast.makeText(MainActivity.this, "Encrypted Data: " + encryptedData, Toast.LENGTH_LONG).show();
                } else {
                    Toast.makeText(MainActivity.this, "No data to access.", Toast.LENGTH_SHORT).show();
                }
            }
        });
    }

    private String encryptData(List<String> data) {
        StringBuilder encryptedData = new StringBuilder();
        for (String line : data) {
            String encryptedLine = encryptLine(line);
            encryptedData.append(encryptedLine).append("\n");
        }
        return encryptedData.toString();
    }

    private String encryptLine(String line) {
        try {
            MessageDigest md = MessageDigest.getInstance("SHA-256");
            byte[] bytes = md.digest(line.getBytes());
            StringBuilder sb = new StringBuilder();
            for (byte b : bytes) {
                sb.append(Integer.toString((b & 0xff) + 0x100, 16).substring(1));
            }
            return sb.toString();
        } catch (NoSuchAlgorithm e) {
            e.printStackTrace();
            return "";
        }
    }
}
```

