import random
import hashlib
import time

class BankAccount:
    def __init__(self, account_number, balance=0):
        self.account_number = account_number
        self.balance = balance

    def deposit(self, amount):
        self.balance += amount

    def withdraw(self, amount):
        if amount <= self.balance:
            self.balance -= amount
        else:
            print("Insufficient funds.")

    def get_balance(self):
        return self.balance

class User:
    def __init__(self, username, password, accounts=None):
        self.username = username
        self.password = self.hash_password(password)
        self.accounts = accounts if accounts else []

    def add_account(self, account):
        self.accounts.append(account)

    def hash_password(self, password):
        return hashlib.sha256(password.encode()).hexdigest()

    def authenticate(self, entered_username, entered_password):
        return self.username == entered_username and self.hash_password(entered_password) == self.password

class BankingSystem:
    def __init__(self):
        self.users = {}
        self.next_account_number = 1001
        self.accounts = []

    def create_user(self, username, password):
        if username not in self.users:
            user = User(username, password)
            self.users[username] = user
            print(f"User {username} created successfully.")
        else:
            print("Username already exists.")

    def login(self, username, password):
        user = self.users.get(username)
        if user and user.authenticate(username, password):
            print(f"Login successful for user {username}.")
            return user
        else:
            print("Login failed. Incorrect username or password.")
            return None

    def create_account(self, user):
        account = BankAccount(self.next_account_number)
        self.next_account_number += 1
        user.add_account(account)
        self.accounts.append(account)
        print(f"Account created for user {user.username} with account number {account.account_number}.")

    def display_accounts(self, user):
        print(f"Accounts for user {user.username}:")
        for idx, account in enumerate(user.accounts, start=1):
            print(f"{idx}. Account Number: {account.account_number}, Balance: ${account.get_balance()}")

def main():
