            password = input("Enter password: ")
            user = banking_system.login(username, password)
            if user:
                print("User accounts:")
                banking_system.display_accounts(user)
        elif choice == '3':
            print("Exiting...")
            break
        else:
            print("Invalid choice. Please select a valid option.")

if __name__ == "__main__":
    main()
