def encrypt_caesar_cipher(message, shift):
    encrypted_message = ""
    for char in message:
        if char.isalpha():
            ascii_value = ord(char)
            if char.islower():
                ascii_value = (ascii_value - ord('a') + shift) % 26 + ord('a')
            else:
                ascii_value = (ascii_value - ord('A') + shift) % 26 + ord('A')
            encrypted_message += chr(ascii_value)
        else:
            encrypted_message += char
    return encrypted_message

def decrypt_caesar_cipher(encrypted_message, shift):
    decrypted_message = ""
    for char in encrypted_message:
        if char.isalpha():
            ascii_value = ord(char)
            if char.islower():
                ascii_value = (ascii_value - shift) % 26 + ord('a')
            else:
                ascii_value = (ascii_value - shift) % 26 + ord('A')
            decrypted_message += chr(ascii_value)
        else:
            decrypted_message += char
    return decrypted_message

